mapper.select:
SELECT id,house_no houseNo,remark_name remarkName,area,address,type,building,unit,room,state, water_type_id waterTypeId,sale,due_time dueTime,electic_type_id electicTypeId,rubbish_type_id rubbishTypeId, property_type_id propertyTypeId,park_no parkNo,`owner`,rubbish_due_time rubbishDueTime FROM ht_house
is_del=0 AND type!=3
AND CONCAT_WS(',',owner,house_no) LIKE CONCAT('%',#{content},'%')
AND type=#{type}
AND state=#{state}
ORDER BY id ASC

SELECT id,house_no houseNo,remark_name remarkName,area,address,type,building,unit,room,state,
due_time dueTime,water_type_id waterTypeId,sale,electic_type_id electicTypeId,rubbish_type_id rubbishTypeId,
property_type_id propertyTypeId,park_no parkNo,`owner`,rubbish_due_time rubbishDueTime FROM ht_house WHERE id=#{id}

SELECT COUNT(1) FROM ht_house 


		house_no=#{houseNo}


		AND id!=#{id}

	AND is_del=0

SELECT u.id,u.`name`,u.phone,u.address,u.create_time createTime,u.update_time updateTime,u.sex, u.birth_date birthDate,u.nickname,u.openid,u.is_use isUse,u.token,h.house_no houseNo,h.type,h.`owner`,h.building,h.unit,h.room,h.rubbish_due_time rubbishDueTime FROM ht_user u LEFT JOIN ht_user_house uh ON u.id=uh.user_id LEFT JOIN ht_house h ON uh.house_id=h.id WHERE h.house_no IN
#{houseNo}
GROUP BY u.id
