mapper.select:
SELECT id,house_no houseNo,remark_name remarkName,area,address,type,building,unit,room,state, water_type_id waterTypeId,sale,due_time dueTime,electic_type_id electicTypeId,rubbish_type_id rubbishTypeId, property_type_id propertyTypeId,park_no parkNo,`owner` FROM ht_house
is_del=0
AND CONCAT_WS(',',owner,house_no) LIKE CONCAT('%',#{content},'%')
AND type=#{type}
AND state=#{state}
ORDER BY id ASC

SELECT id,house_no houseNo,remark_name remarkName,area,address,type,building,unit,room,state, due_time dueTime,water_type_id waterTypeId,sale,electic_type_id electicTypeId,rubbish_type_id rubbishTypeId, property_type_id propertyTypeId,park_no parkNo,`owner` FROM ht_house
is_del=0 AND type!=3
AND CONCAT_WS(',',owner,house_no) LIKE CONCAT('%',#{content},'%')
AND type=#{type}
AND state=#{state}
ORDER BY id DESC,due_time ASC

SELECT id,house_no houseNo,remark_name remarkName,area,address,type,building,unit,room,state,
due_time dueTime,water_type_id waterTypeId,sale,electic_type_id electicTypeId,rubbish_type_id rubbishTypeId,
property_type_id propertyTypeId,park_no parkNo,`owner` FROM ht_house WHERE id=#{id}

SELECT 1 FROM ht_house 


		house_no=#{house_no}


		AND id!=#{id}

	AND is_del=0
