mapper.select:
SELECT id,house_no houseNo,upload_time uploadTime,is_pay isPay,pay_time payTime,type,`month`,
money,dosage,`explain` FROM ht_house_consume


		house_no=#{content}


		`month`=#{month}


		is_pay=#{isPay}


		type=#{type}


ORDER BY id ASC

SELECT id,house_no houseNo,upload_time uploadTime,is_pay isPay,pay_time payTime,type,`month`,
money,dosage,`explain` FROM ht_house_consume 
WHERE id=#{id}
