mapper.select:
SELECT id,house_no houseNo,car_no carNo,park_no parkNo,owner_one ownerOne,owner_two ownerTwo,
phone_one phoneOne,phone_two phoneTwo,type,due_time dueTime,norm_id normId,area,genre
FROM ht_park WHERE id=#{id}

SELECT id,house_no houseNo,car_no carNo,park_no parkNo,owner_one ownerOne,owner_two ownerTwo, phone_one phoneOne,phone_two phoneTwo,type,due_time dueTime,norm_id normId,area,genre FROM ht_park
is_del=0
AND type=#{type}
AND genre=#{genre}
AND CONCAT_WS(',',owner_one,owner_one,park_no,car_no) LIKE CONCAT('%',#{content},'%')
ORDER BY id DESC,due_time ASC

SELECT id,house_no houseNo,car_no carNo,park_no parkNo,owner_one ownerOne,owner_two ownerTwo, phone_one phoneOne,phone_two phoneTwo,type,due_time dueTime,norm_id normId,area,genre FROM ht_park
is_del=0
AND CONCAT_WS(',',owner_one,owner_one,park_no,car_no) LIKE CONCAT('%',#{content},'%')
AND type=#{type}
AND genre=#{genre}
ORDER BY id ASC

SELECT 1 FROM ht_park

	is_del=0

		AND park_no=#{parkNo}


		AND id!=#{id}
